import React, {Component} from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import {robots} from './robots';


class App extends Component{
    constructor(){
        super()
        this.state = {
            robots:robots,
            searchfiled: ''
        }
    }

    onSearchChange(event){
        const filteredfobots = this.state.robots.filter(robot => {
            return robots.name.toLowerCase().inlcudes(this.state.searchfiled.toLowerCase());
        })        
        console.log(filteredfobots);

    }
    render(){
        return (
            <div className = 'tc'>
            <h1>RobotFriends</h1>
            <SearchBox searchChange = {this.onSearchChange}/>
            <CardList robots = {this.state.robots}/>
            </div>
        );
    }
}
export default App;
